import React from "react";
import { useNavigate } from "react-router-dom";
import "./TemplateSelection.css";
import resume1 from "../Images/Tresume1.png";
import resume2 from "../Images/Tresume2.png";
import resume3 from "../Images/Tresume3.png";
import resume4 from "../Images/Tresume4.png";

// ... import more as needed

const templates = [
    {
      id: 1,
      image: resume1,
      canvaLink: "https://www.canva.com/design/DAGkg3mushY/4VWgmvAvRGgT3NG9E4nTMQ/edit?utm_content=DAGkg3mushY&utm_campaign=designshare&utm_medium=link2&utm_source=sharebutton"
    },
    {
      id: 2,
      image: resume2,
      canvaLink: "https://www.canva.com/design/DAGkg_fNCb4/KTbKpN-mB7AilNO17Awj1g/edit?utm_content=DAGkg_fNCb4&utm_campaign=designshare&utm_medium=link2&utm_source=sharebutton"
    },
    {
      id: 3,
      image: resume3,
      canvaLink: "https://www.canva.com/design/DAGkg2C3XxM/jMHjIvcSjG2kI_-viZzwww/edit?utm_content=DAGkg2C3XxM&utm_campaign=designshare&utm_medium=link2&utm_source=sharebutton"
    },
    {
        id:4,
        image:resume4,
        canvaLink:"https://www.canva.com/design/DAGkg38Mjwg/IaMfB15lmIUei88IfyhhJw/edit?utm_content=DAGkg38Mjwg&utm_campaign=designshare&utm_medium=link2&utm_source=sharebutton"
    }
  ];
  

  const TemplateSelection = () => {
    const handleTemplateClick = (canvaLink) => {
      // Open Canva template in a new tab
      window.open(canvaLink, "_blank");
    };
  
    return (
      <div className="template-selection-container">
        <h2>Select Your Resume Template</h2>
        <p>Choose a design that suits your style and profession.</p>
        <div className="template-grid">
          {templates.map((template) => (
            <div
              key={template.id}
              className="template-card"
              onClick={() => handleTemplateClick(template.canvaLink)}
            >
              <img src={template.image} alt={template.name} />
              <h4>{template.name}</h4>
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default TemplateSelection;
