// Editor.js
import React, { useState } from "react";
import resume1 from "../Images/resume1.png";
import resume2 from "../Images/resume2.png";
import resume3 from "../Images/resume3.png";
// ... add rest

const templateMap = {
  1: resume1,
  2: resume2,
  3: resume3,
  // ... map others
};

function ResumeBuilder() {
  // Manage state directly in ResumeBuilder
  const [selectedTemplate, setSelectedTemplate] = useState(null);
  const [userInfo, setUserInfo] = useState({
    fullName: "John Doe",
    jobTitle: "Software Engineer",
    email: "johndoe@example.com",
    // Add more fields as needed
  });

  const handleTemplateSelect = (templateId) => {
    setSelectedTemplate(templateId);
  };

  return (
    <div className="resume-builder">
      <div className="template-selection">
        <h2>Select a Template</h2>
        <div className="templates">
          {Object.keys(templateMap).map((key) => (
            <img
              key={key}
              src={templateMap[key]}
              alt={`Template ${key}`}
              className={`template-preview ${selectedTemplate === parseInt(key) ? "selected" : ""}`}
              onClick={() => handleTemplateSelect(parseInt(key))}
            />
          ))}
        </div>
      </div>
      <Editor selectedTemplate={selectedTemplate} userInfo={userInfo} />
    </div>
  );
}

function Editor({ selectedTemplate, userInfo }) {
  const templateImage = templateMap[selectedTemplate];

  return (
    <div className="editor-page">
      {templateImage ? (
        <img src={templateImage} alt="Selected Template" className="editor-template-preview" />
      ) : (
        <p>Please select a template to preview.</p>
      )}
      <div className="resume-info">
        <h1>{userInfo.fullName}</h1>
        <h3>{userInfo.jobTitle}</h3>
        <p>{userInfo.email}</p>
        {/* Expand with skills, experience, etc., later */}
      </div>
    </div>
  );
}

export default ResumeBuilder;
