import React, { useState } from "react";
import { FaFacebook, FaGoogle, FaLinkedin, FaEnvelope } from "react-icons/fa";
import axios from "axios";
import "./Signinup.css";

// Modal Component
const CreateAccountModal = ({ show, onClose, onAccountCreated }) => {
  const [errorMessage, setErrorMessage] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  // The modal is conditionally rendered but hooks are always called in the same order
  if (!show) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newUser = {
      username: e.target.username.value,
      email: e.target.email.value,
      password: e.target.password.value,
    };

    try {
      const response = await axios.post("http://localhost:5000/api/signup", newUser);
      console.log("Account created:", response.data);

      // Clear any previous messages
      setErrorMessage(null);
      setSuccessMessage("Account successfully created!");

      // Notify the parent to update the UI and close the modal
      onAccountCreated();
      onClose(); // Close the modal
    } catch (error) {
      console.error("Error creating account:", error);

      // Set the error message
      setErrorMessage("Error creating account. Please try again.");
      setSuccessMessage(null); // Clear success message if there's an error
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>X</button>
        <h2>Create Account</h2>

        {/* Display success or error message */}
        {successMessage && <div className="success-message">{successMessage}</div>}
        {errorMessage && <div className="error-message">{errorMessage}</div>}

        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>Username</label>
            <input type="text" name="username" placeholder="Enter your username" required />
          </div>
          <div className="input-group">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter your email" required />
          </div>
          <div className="input-group">
            <label>Password</label>
            <input type="password" name="password" placeholder="Enter your password" required />
          </div>
          <button type="submit" className="submit-btn">Create Account</button>
        </form>
      </div>
    </div>
  );
};

const Signinup = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [feedbackMessage, setFeedbackMessage] = useState(null); // Store feedback message

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  // This function is passed down to the modal, and is called after a successful account creation
  const handleAccountCreated = () => {
    setFeedbackMessage("Account created successfully! You can now log in.");
  };

  return (
    <div className="signin-page">
      <nav className="navbar1">
        <div className="nav-icons1">
          <p className="webname1">
            <span className="large-text1">resume.com</span>
            <span className="small-text1">by smoother</span>
          </p>
        </div>
      </nav>

      <div className="Container1">
        <div className="centercontent">
          <h1>Log In</h1>
          <p>We are happy to see you back!</p>

          <div className="signinoptions">
            <button className="facebook">
              <FaFacebook className="icon" /> Facebook
            </button>
            <button className="google">
              <FaGoogle className="icon" /> Google
            </button>
            <button className="linkedin">
              <FaLinkedin className="icon" /> LinkedIn
            </button>
            <button className="email">
              <FaEnvelope className="icon" /> Email
            </button>
          </div>

          <p>
            I am not registered yet.{" "}
            <a href="#" onClick={openModal}>Sign Up</a>
          </p>

          {/* Display feedback message */}
          {feedbackMessage && <div className="feedback-message">{feedbackMessage}</div>}
        </div>
      </div>

      {/* Modal for Create Account */}
      <CreateAccountModal show={isModalOpen} onClose={closeModal} onAccountCreated={handleAccountCreated} />
    </div>
  );
};

export default Signinup;
