import React, { useState } from "react";
import { FaFacebook, FaGoogle, FaLinkedin, FaEnvelope } from "react-icons/fa";
import axios from "axios";
import "./Signinup.css";

// Modal Component for Sign Up
const CreateAccountModal = ({ show, onClose, onAccountCreated }) => {
  if (!show) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newUser = {
      username: e.target.username.value,
      email: e.target.email.value,
      password: e.target.password.value,
    };

    try {
      const response = await axios.post("http://localhost:5000/api/signup", newUser);
      console.log("Account created:", response.data);

      // Show success alert
      window.alert("Account successfully created!");

      // Notify the parent to update the UI and close the modal
      onAccountCreated();
      onClose(); // Close the modal
    } catch (error) {
      console.error("Error creating account:", error);

      // Show error alert
      window.alert("Error creating account. Please try again.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>X</button>
        <h2>Create Account</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>Username</label>
            <input type="text" name="username" placeholder="Enter your username" required />
          </div>
          <div className="input-group">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter your email" required />
          </div>
          <div className="input-group">
            <label>Password</label>
            <input type="password" name="password" placeholder="Enter your password" required />
          </div>
          <button type="submit" className="submit-btn">Create Account</button>
        </form>
      </div>
    </div>
  );
};

const Signinup = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [users, setUsers] = useState([]);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  // This function simulates checking the user credentials
  const handleLogin = () => {
    // In a real app, you'd fetch the data from the server, but here we simulate with a JSON file
    axios.get("http://localhost:5000/users.json") // This is your user data in user.json
      .then(response => {
        const users = response.data;
        const user = users.find(u => u.email === email && u.password === password);

        if (user) {
          window.alert(`${user.username} is successfully logged in!`);
        } else {
          window.alert("Invalid email or password.");
        }
      })
      .catch(error => {
        console.error("Error fetching users:", error);
        window.alert("There was an error verifying your credentials.");
      });
  };

  return (
    <div className="signin-page">
      <nav className="navbar1">
        <div className="nav-icons1">
          <p className="webname1">
            <span className="large-text1">resume.com</span>
            <span className="small-text1">by smoother</span>
          </p>
        </div>
      </nav>

      <div className="Container1">
        <div className="centercontent">
          <h1>Log In</h1>
          <p>We are happy to see you back!</p>

          {/* Login Form (Email and Password) */}
          <div className="login-form">
            <div className="input-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="input-group">
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            <button className="login-btn" onClick={handleLogin}>Login</button>
          </div>

          <div className="signinoptions">
            <button className="facebook">
              <FaFacebook className="icon" /> Facebook
            </button>
            <button className="google">
              <FaGoogle className="icon" /> Google
            </button>
            <button className="linkedin">
              <FaLinkedin className="icon" /> LinkedIn
            </button>
            <button className="email">
              <FaEnvelope className="icon" /> Email
            </button>
          </div>

          <p>
            I am not registered yet.{" "}
            <a href="#" onClick={openModal}>Sign Up</a>
          </p>
        </div>
      </div>

      {/* Modal for Create Account */}
      <CreateAccountModal show={isModalOpen} onClose={closeModal} />
    </div>
  );
};

export default Signinup;
